SELECT non_negative_difference(last(cumulative_sum)) FROM (
SELECT cumulative_sum(non_negative_difference(last("totalbytesreceived"))) FROM /^$measurement$/ WHERE  ("box" =~ /^$boxtag$/) AND $timeFilter GROUP BY time($__interval)
) WHERE $timeFilter GROUP BY time(1d) tz(${timezone:singlequote})

